local tewl = Instance.new("Tool",game.Players.Infernal_Keeper.Backpack)
tewl.TextureId = "http://www.roblox.com/asset/?id=109353648"

local slash = Instance.new("Animation",tewl)
slash.AnimationId = "http://www.roblox.com/Asset?ID=49815113"

local slashs = Instance.new("Sound",weapon.Handle)
slashs.SoundId = "http://www.roblox.com/Asset?ID=109362710"

local han = Instance.new("Part",tewl)
han.Name = "Handle"
han.CanCollide = false
han.Size = Vector3.new(0,0,0)
han.Transparency = 1
local attack = false

tewl.Equipped:connect(function()
	for i, v in pairs(weapon:GetChildren()) do
		if v.Name ~= "Handle" and v.Name ~= "Hitbox" then
			v.Transparency = 0
		end
	end
	for i, v in pairs(weapon2:GetChildren()) do
		if v.Name ~= "Handle2" then
			v.Transparency = 0
		end
	end
	LSH.Parent = nil
	LW.Parent = char.Torso
end)

tewl.Unequipped:connect(function()
	for i, v in pairs(weapon:GetChildren()) do
		if v.Name ~= "Handle" and v.Name ~= "Hitbox" then
			v.Transparency = 1
		end
	end
	for i, v in pairs(weapon2:GetChildren()) do
		if v.Name ~= "Handle2" then
			v.Transparency = 1
		end
	end
	LSH.Parent = char.Torso
	LW.Parent = nil
end)

tewl.Activated:connect(function()
	if attack == false then
		attack = true
		local anim = char.Humanoid:LoadAnimation(slash)
		anim:Play()
		slashs:Play()
		magn(7,weapon.Hitbox,4,"http://www.roblox.com/Asset?ID=109362738",1,1,Color3.new(1,0,0))
		wait(.2)
		slashs:Play()
		magn(7,weapon.Hitbox,4,"http://www.roblox.com/Asset?ID=109362738",1,1,Color3.new(1,0,0))
		wait(.6)
		attack = false
	end
end)

while true do
	if attack == false then
	LW.C0 = LW.C0:lerp(CFrame.new(-1, 0.5, -.5) * CFrame.Angles(math.rad(90), math.rad(0), math.rad(60)), 0.3)
	end
	wait()
end